/**
 * Start all tasks contained in this component.
 */
void ComponentAASBridge::startAllTasks() {
	// start task CommandActivity
	if(connections.commandActivity.scheduler != "DEFAULT") {
		ACE_Sched_Params commandActivity_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.commandActivity.scheduler == "FIFO") {
			commandActivity_SchedParams.policy(ACE_SCHED_FIFO);
			#if defined(ACE_HAS_PTHREADS)
				commandActivity_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
			#elif defined (ACE_HAS_WTHREADS)
				commandActivity_SchedParams.priority(THREAD_PRIORITY_IDLE);
			#endif
		} else if(connections.commandActivity.scheduler == "RR") {
			commandActivity_SchedParams.policy(ACE_SCHED_RR);
			#if defined(ACE_HAS_PTHREADS)
				commandActivity_SchedParams.priority(ACE_THR_PRI_RR_MIN);
			#elif defined (ACE_HAS_WTHREADS)
				commandActivity_SchedParams.priority(THREAD_PRIORITY_IDLE);
			#endif
		}
		commandActivity->start(commandActivity_SchedParams, connections.commandActivity.cpuAffinity);
	} else {
		commandActivity->start();
	}
}

