int RobotTask::on_execute() {

    // create a laser communication object and read it from the input port
    CommBasicObjects::CommMobileLaserScan scan;
    Smart::StatusCode status = this->laserServiceInGetUpdate(scan);
    if (status != Smart::SMART_OK) {
        std::cerr << "Getting laser scan failed: " << status << std::endl;
        sleep(2);
        return 0;
    }

    double vx = 0.0; // speed in x-axis direction (forward) [m/s]
    double vy = 0.0; // speed in y-axis direction (left) [m/s]
    double v_rotate = 0.0; // turn speed (counterclockwise) [radians/s]

    // Start of your code

    // calculate vx, vy, v_rotate based on laser scan

    // End of your code

    CommBasicObjects::CommNavigationVelocity comNavVel;
    comNavVel.set_vX(vx, 1); // meters per second
    comNavVel.set_vY(vy, 1); // meters per second
    comNavVel.set_omega(v_rotate);
    status = this->navigationVelocityServiceOutPut(comNavVel);
    if (status != Smart::SMART_OK) {
        std::cerr << "error navigationVelocityServiceOutPut " << status << std::endl;
        sleep(1);
    } else {
        std::cout << "Updating velocity " << comNavVel << std::endl;
    }
    return 0;
}

