Smart::StatusCode ComponentKB::connectKbChainedEntriesEventClient(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.kbChainedEntriesEventClient.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = kbChainedEntriesEventClient->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->kbChainedEntriesEventClient->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	return status;
}


