Smart::StatusCode ComponentRGBToWSStream::connectVideoImageClient(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.videoImageClient.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = videoImageClient->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->videoImageClient->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	videoImageClient->subscribe(connections.videoImageClient.interval);
	return status;
}


