Smart::StatusCode ComponentRTABSlam::connectBaseStatePushNewestClient(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.baseStatePushNewestClient.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = baseStatePushNewestClient->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->baseStatePushNewestClient->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	baseStatePushNewestClient->subscribe(connections.baseStatePushNewestClient.interval);
	return status;
}
