/**
 * First connect ALL client ports contained in this component, then start all services:
 * activate state, push, etc...
 */
Smart::StatusCode ComponentRTABSlam::connectAndStartAllServices() {
	Smart::StatusCode status = Smart::SMART_OK;
	
	status = connectBaseStatePushNewestClient(connections.baseStatePushNewestClient.serverName, connections.baseStatePushNewestClient.serviceName);
	if(status != Smart::SMART_OK) return status;
	status = connectRgbd_client(connections.rgbd_client.serverName, connections.rgbd_client.serviceName);
	if(status != Smart::SMART_OK) return status;
	return status;
}

/**
 * Start all tasks contained in this component.
 */
void ComponentRTABSlam::startAllTasks() {
	// start task LocalizationThread
	if(connections.localizationThread.scheduler != "DEFAULT") {
		ACE_Sched_Params localizationThread_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.localizationThread.scheduler == "FIFO") {
			localizationThread_SchedParams.policy(ACE_SCHED_FIFO);
			localizationThread_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.localizationThread.scheduler == "RR") {
			localizationThread_SchedParams.policy(ACE_SCHED_RR);
			localizationThread_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		localizationThread->start(localizationThread_SchedParams, connections.localizationThread.cpuAffinity);
	} else {
		localizationThread->start();
	}
	// start task MappingThread
	if(connections.mappingThread.scheduler != "DEFAULT") {
		ACE_Sched_Params mappingThread_SchedParams(ACE_SCHED_OTHER, ACE_THR_PRI_OTHER_DEF);
		if(connections.mappingThread.scheduler == "FIFO") {
			mappingThread_SchedParams.policy(ACE_SCHED_FIFO);
			mappingThread_SchedParams.priority(ACE_THR_PRI_FIFO_MIN);
		} else if(connections.mappingThread.scheduler == "RR") {
			mappingThread_SchedParams.policy(ACE_SCHED_RR);
			mappingThread_SchedParams.priority(ACE_THR_PRI_RR_MIN);
		}
		mappingThread->start(mappingThread_SchedParams, connections.mappingThread.cpuAffinity);
	} else {
		mappingThread->start();
	}
}

