Smart::StatusCode ComponentRackDetection::connectKinectQueryClient(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.kinectQueryClient.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = kinectQueryClient->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->kinectQueryClient->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	return status;
}


