Smart::StatusCode ComponentRealSensePersonTracker::connectRealSenseClient(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.realSenseClient.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = realSenseClient->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->realSenseClient->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	realSenseClient->subscribe(connections.realSenseClient.interval);
	return status;
}


