		double temp_initX = 0.0;
		if(request.getDouble("initX", temp_initX) != 0) {
			answer.setResponse(SmartACE::ParamResponseType::INVALID);
			std::cout<<"ParamUpdateHandler - error parsing value: initX request: "<<request<<std::endl;
		}
		double temp_initY = 0.0;
