void ParamUpdateHandler::loadParameter(SmartACE::SmartIniParameter &parameter)
{
	/*
	 Parameters can be specified via command line -filename=<filename>

	 With this parameter present:
	 - The component will look for the file in the current working directory,
	 a path relative to the current directory or any absolute path
	 - The component will use the default values if the file cannot be found

	 With this parameter absent:
	 - <Name of Component>.ini will be read from current working directory, if found there
	 - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	 - Default values will be used if neither found in working directory or /etc
	 */

	// load parameters
	try
	{
		// print all known parameters
		parameter.print();

		//
		// load internal parameters (if any)
		//
		// parameter Settings
		if(parameter.getBoolean("Settings", "display_depth_image", commitState.Settings.display_depth_image))
		{
			globalState.Settings.display_depth_image = commitState.Settings.display_depth_image;
		}
		if(parameter.getString("Settings", "pt_data", commitState.Settings.pt_data))
		{
			globalState.Settings.pt_data = commitState.Settings.pt_data;
		}
		if(parameter.getBoolean("Settings", "withDebugWindow", commitState.Settings.withDebugWindow))
		{
			globalState.Settings.withDebugWindow = commitState.Settings.withDebugWindow;
		}
		
		//
		// load extended parameters (if any)
		//
		
		//
		// load instance parameters (if a parameter definition was instantiated in the model)
		//

	} catch (const SmartACE::IniParameterError & e)
	{
		std::cerr << "Exception from parameter handling: " << e << std::endl;
	} catch (const std::exception &ex)
	{
		std::cerr << "Uncaught std:: exception" << ex.what() << std::endl;
	} catch (...)
	{
		std::cerr << "Uncaught exception" << std::endl;
	}
}
