#include "SmartStateChangeHandler.hh"
#include "ComponentRobotToRobotDocking.hh"

#include <iostream>

// Called when a substate is entered
void SmartStateChangeHandler::handleEnterState(const std::string & substate) throw()
{
	if(substate == "LaserDocking"){
		CommRobotinoObjects::RobotinoDockingEventState goalEventState;
		goalEventState.set(CommRobotinoObjects::RobotinoDockingEventType::LASER_DOCKING_NOT_DONE);
			COMP->dockingEventServer->put(goalEventState);
			std::cout<<"GOALD EVENT LASER_DOCKING_NOT_DONE FIRED!"<<std::endl;
		COMP->laserDockingTask->start();
	}
	if(substate == "UnDocking"){
		CommRobotinoObjects::RobotinoDockingEventState goalEventState;
		goalEventState.set(CommRobotinoObjects::RobotinoDockingEventType::UN_DOCKING_NOT_DONE);
		COMP->dockingEventServer->put(goalEventState);
		std::cout<<"GOALD EVENT UN_DOCKING_NOT_DONE FIRED!"<<std::endl;
		COMP->undockingTask->undockInit();

	}

}

// Called when a substate is left
void SmartStateChangeHandler::handleQuitState(const std::string & substate) throw()
{
	if(substate == "LaserDocking"){
		COMP->laserDockingTask->stop();
	}
}
