#include "ManualUnLoadTask.hh"
#include "ComponentRobotinoConveyerBeltServer_OPCUA.hh"

#include <iostream>

ManualUnLoadTask::ManualUnLoadTask(SmartACE::SmartComponent *comp) 
:	ManualUnLoadTaskCore(comp)
{
	std::cout << "constructor ManualUnLoadTask\n";

	unload_signal_bit = COMP->getGlobalState().getRobot().getManual_load_dout();
	box_present_bit = COMP->getGlobalState().getRobot().getBox_present_din();
	ack_pressed_bit = COMP->getGlobalState().getRobot().getAck_pressed_din();
	first_run = true;
}
ManualUnLoadTask::~ManualUnLoadTask() 
{
	std::cout << "destructor ManualUnLoadTask\n";
}



int ManualUnLoadTask::on_entry()
{
	// do initialization procedures here, which are called once, each time the task is started
	// it is possible to return != 0 (e.g. when initialization fails) then the task is not executed further
	return 0;
}
int ManualUnLoadTask::on_execute()
{
	// this method is called from an outside loop,
	// hence, NEVER use an infinite loop (like "while(1)") here inside!!!
	// also do not use blocking calls which do not result from smartsoft kernel
	
	Smart::StatusCode status;
	CommRobotinoObjects::RobotinoConveyerBeltEventState eventState;

	ParameterStateStruct localState = COMP->getGlobalState();


	status = COMP->stateSlave->acquire("manualunload");
	if (status == Smart::SMART_OK)
	{


		std::cout<<"ManualUnLoadTask "<<std::endl;
		if (first_run == true && queryDigitalInput(box_present_bit)==false){
			eventState.set(CommRobotinoObjects::RobotinoConveyerBeltEventType::CONVEYER_BELT_UNLOAD_ERROR_NO_BOX);
			COMP->robotinoConveyerBeltEventOut->put(eventState);
			std::cout<<"ERROR: unloading triggered - no box on the belt!"<<std::endl;
		} else {

			SmartACE::SmartGuard guard(lock);
			queryDigitalOutput(unload_signal_bit,true);
			if (queryDigitalInput(ack_pressed_bit) == true){
				//wait until the button is released
				while(queryDigitalInput(ack_pressed_bit) == true){
					ACE_OS::sleep(ACE_Time_Value(0,500000));
				}
				std::cout<<"ManualUnLoadTask: ACK - ";
				if(queryDigitalInput(box_present_bit) == false){
					std::cout<<"no box present"<<std::endl;
					eventState.set(CommRobotinoObjects::RobotinoConveyerBeltEventType::CONVEYER_BELT_UNLOAD_DONE);
					COMP->robotinoConveyerBeltEventOut->put(eventState);

				} else {
					std::cout<<"box present"<<std::endl;
					eventState.set(CommRobotinoObjects::RobotinoConveyerBeltEventType::CONVEYER_BELT_UNLOAD_ERROR_BOX_STILL_PRESENT);
					COMP->robotinoConveyerBeltEventOut->put(eventState);
				}
			}

		}
		first_run = false;

		COMP->stateSlave->release("manualunload");
	} else {
		std::cout<<" [ManualunloadTask] acquire state: "<<Smart::StatusCodeConversion(status)<<std::endl;
	}


	// it is possible to return != 0 (e.g. when the task detects errors), then the outer loop breaks and the task stops
	return 0;
}

void ManualUnLoadTask::disableUnLoadSignal(){
	SmartACE::SmartGuard guard(lock);
	queryDigitalOutput(unload_signal_bit,false);
}

void ManualUnLoadTask::reset(){
	first_run = true;
}

int ManualUnLoadTask::on_exit()
{
	// use this method to clean-up resources which are initialized in on_entry() and needs to be freed before the on_execute() can be called again
	return 0;
}

bool ManualUnLoadTask::queryDigitalInput(const unsigned int & bit){

	CommBasicObjects::CommIOValues inputQuery;
	CommBasicObjects::CommIOValues inputQueryResult;


	Smart::StatusCode status;
	status = COMP->commIOValuesQueryServiceReq->query(inputQuery,inputQueryResult);
	if(status != Smart::SMART_OK){
		std::cout<<__FUNCTION__<<" ERROR: query digital IO"<<std::endl;
		COMP->stateSlave->setWaitState("FatalError");
		return false;
	}
	if(bit>inputQueryResult.getDigitalInputValuesSize()){
		std::cout<<__FUNCTION__<<" ERROR: query bit: "<<bit<<" digital IO size: "<<inputQueryResult.getDigitalInputValuesSize()<<std::endl;
		COMP->stateSlave->setWaitState("FatalError");
		return false;
	}
	return inputQueryResult.getDigitalInputValuesElemAtPos(bit);

}

void ManualUnLoadTask::queryDigitalOutput(const unsigned int & bit, const bool & value){

	CommBasicObjects::CommIOValues outputQuery;
	CommBasicObjects::CommIOValues dummy;
	outputQuery.resizeDigitalOutputValues(1);

	Smart::StatusCode status;
	CommBasicObjects::CommDigitalOutputRequest digitalOutputRequest(bit,value);

	outputQuery.setDigitalOutputValuesElemAtPos(0,digitalOutputRequest);

	status = COMP->commIOValuesQueryServiceReq->query(outputQuery,dummy);
	if(status != Smart::SMART_OK){
		std::cout<<__FUNCTION__<<" ERROR: query digital IO"<<std::endl;
		COMP->stateSlave->setWaitState("FatalError");
	}

}



