Smart::StatusCode ComponentSlcPacking::connectObjectPropertyQueryClient(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.objectPropertyQueryClient.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = objectPropertyQueryClient->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->objectPropertyQueryClient->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	return status;
}
