#include "PlannerGridTask.hh"
#include "ComponentVisualization.hh"

#include <iostream>

PlannerGridTask::PlannerGridTask(SmartACE::SmartComponent *comp) 
:	PlannerGridTaskCore(comp)
{
	std::cout << "constructor PlannerGridTask\n";
}
PlannerGridTask::~PlannerGridTask() 
{
	std::cout << "destructor PlannerGridTask\n";
}
int PlannerGridTask::connectServices(){
	std::cout << "connecting to: " << COMP->connections.plannerWavefrontGridMap.serverName << "; " << COMP->connections.plannerWavefrontGridMap.serviceName << std::endl;
	Smart::StatusCode status = COMP->plannerWavefrontGridMap->connect(COMP->connections.plannerWavefrontGridMap.serverName, COMP->connections.plannerWavefrontGridMap.serviceName);
	while (status != Smart::SMART_OK) {
		usleep(500000);
		status = COMP->plannerWavefrontGridMap->connect(COMP->connections.plannerWavefrontGridMap.serverName, COMP->connections.plannerWavefrontGridMap.serviceName);
	}
	std::cout << COMP->connections.plannerWavefrontGridMap.serverName << "; " << COMP->connections.plannerWavefrontGridMap.serviceName << " connected.\n";
	return 0;
}


int PlannerGridTask::disconnectServices(){
	COMP->plannerWavefrontGridMap->disconnect();
	return 0;
}

int PlannerGridTask::on_entry()
{
	GridMapVisualization::VizConfig config;
	config.identifier = "PlannerMap";
	config.activateTransparency = true;
	config.showAxis = false;
	config.mapType = GridMapVisualization::MapType::PLANNER_MAP;

	gridMap = new GridMapVisualization(COMP->getWindow3d(), config);
	COMP->plannerWavefrontGridMap->subscribe();

	return (gridMap !=0)?0:1;
}
int PlannerGridTask::on_execute()
{
	Smart::StatusCode status = COMP->plannerWavefrontGridMap->getUpdateWait(map);
	if (status == Smart::SMART_OK) {
		gridMap->displayGridMap(map);
	} else {
		gridMap->clear();
	}	return 0;
}
int PlannerGridTask::on_exit()
{
	delete gridMap;
	COMP->curPushClient->unsubscribe();
	return 0;
}
