Smart::StatusCode ComponentWebotsConveyorBeltOpcua::connectTrafficLightsServiceIn(const std::string &serverName, const std::string &serviceName) {
	Smart::StatusCode status;
	
	if(connections.trafficLightsServiceIn.initialConnect == false) {
		return Smart::SMART_OK;
	}
	std::cout << "connecting to: " << serverName << "; " << serviceName << std::endl;
	status = trafficLightsServiceIn->connect(serverName, serviceName);
	while(status != Smart::SMART_OK)
	{
		ACE_OS::sleep(ACE_Time_Value(0,500000));
		status = COMP->trafficLightsServiceIn->connect(serverName, serviceName);
	}
	std::cout << "connected.\n";
	trafficLightsServiceIn->subscribe(connections.trafficLightsServiceIn.interval);
	return status;
}


