void PowerOutputSendInHandler::on_PowerOutputSendIn(const CommRobotinoObjects::CommRobotinoPowerOutputValue &input)
{
    Smart::StatusCode status = COMP->stateSlave->tryAcquire("eStop");
    if(status == Smart::SMART_OK){
    	std::cout<<__FUNCTION__<<": Robot in eStop, not sending power output command!"<<std::endl;
    	COMP->stateSlave->release("eStop");
    } else {

		if(COMP->robot!=NULL){
			COMP->robot->setPowerOutput(input.getValue());
		} else {
			std::cout<<"Robot is not initialized!"<<std::endl;
		}
    }

}
