// run the component
void SmartFestoFleetCom::run()
{
	stateSlave->acquire("init");
	// startup all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->onStartup();
	}
	
	// startup all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->onStartup();
	}
	stateSlave->release("init");
	
	// do not call this handler within the init state (see above) as this handler internally calls setStartupFinished() (this should be fixed in future)
	compHandler.onStartup();
	
	// this call blocks until the component is commanded to shutdown
	stateSlave->acquire("shutdown");
	
	// shutdown all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->onShutdown();
	}
	
	// shutdown all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->onShutdown();
	}
	
	if(connections.component.useLogger == true) {
		//FIXME: use logging
		//Smart::LOGGER->stopLogging();
	}
	
	compHandler.onShutdown();
	
	stateSlave->release("shutdown");
}

// clean-up component's resources
void SmartFestoFleetCom::fini()
{
	// unlink all observers
	
	// destroy all task instances
	// unlink all UpcallManagers
	taskResultInUpcallManager->detach(commandResultTask);
	// unlink the TaskTrigger
	if(commandResultTaskTrigger != NULL){
		commandResultTaskTrigger->detach(commandResultTask);
		delete commandResultTask;
	}
	// unlink all UpcallManagers
	// unlink the TaskTrigger
	if(readCommandTaskTrigger != NULL){
		readCommandTaskTrigger->detach(readCommandTask);
		delete readCommandTask;
	}
	// unlink all UpcallManagers
	// unlink the TaskTrigger
	if(statusSerialTaskTrigger != NULL){
		statusSerialTaskTrigger->detach(statusSerialTask);
		delete statusSerialTask;
	}

	// destroy all input-handler

	// destroy InputTaskTriggers and UpcallManagers
	delete laserSafetyEventServiceInInputTaskTrigger;
	delete laserSafetyEventServiceInUpcallManager;
	delete laserSafetyEventServiceInInputCollector;
	delete taskResultInInputTaskTrigger;
	delete taskResultInUpcallManager;
	delete taskResultInInputCollector;

	// destroy client ports
	delete laserSafetyEventServiceIn;
	delete taskResultIn;
	delete kbQueryClient;

	// destroy request-handlers

	// destroy server ports
	delete taskEventOutWrapper;
	delete taskEventOut;
	
	// destroy event-test handlers (if needed)
	taskEventOutEventTestHandler;
	
	delete stateSlave;
	delete stateActivityManager;
	// destroy state-change-handler
	delete stateChangeHandler;
	
	// destroy all master/slave ports
	delete wiringSlave;
	delete param;
	

	// destroy all registered component-extensions
	for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
	{
		extension->second->destroy();
	}

	// destroy all registered port-factories
	for(auto portFactory = portFactoryRegistry.begin(); portFactory != portFactoryRegistry.end(); portFactory++) 
	{
		portFactory->second->destroy();
	}
	
}

void SmartFestoFleetCom::loadParameter(int argc, char *argv[])
{
	/*
	 Parameters can be specified via command line --filename=<filename> or -f <filename>

	 With this parameter present:
	   - The component will look for the file in the current working directory,
	     a path relative to the current directory or any absolute path
	   - The component will use the default values if the file cannot be found

	 With this parameter absent:
	   - <Name of Component>.ini will be read from current working directory, if found there
	   - $SMART_ROOT/etc/<Name of Component>.ini will be read otherwise
	   - Default values will be used if neither found in working directory or /etc
	 */
	SmartACE::SmartIniParameter parameter;
	std::ifstream parameterfile;
	bool parameterFileFound = false;

	// load parameters
	try
	{
		// if paramfile is given as argument
		if(parameter.tryAddFileFromArgs(argc,argv,"filename", 'f'))
		{
			parameterFileFound = true;
			std::cout << "parameter file is loaded from an argv argument \n";
		} else if(parameter.searchFile("SmartFestoFleetCom.ini", parameterfile)) {
			parameterFileFound = true;
			std::cout << "load SmartFestoFleetCom.ini parameter file\n";
			parameter.addFile(parameterfile);
		} else {
			std::cout << "WARNING: SmartFestoFleetCom.ini parameter file not found! (using default values or command line arguments)\n";
		}
		
		// add command line arguments to allow overwriting of parameters
		// from file
		parameter.addCommandLineArgs(argc,argv,"component");
		
		// initialize the naming service using the command line parameters parsed in the
		// SmartIniParameter class. The naming service parameters are expected to be in
		// the "component" parameter group.
		SmartACE::NAMING::instance()->checkForHelpArg(argc,argv);
		if(parameterFileFound) 
		{
			if(SmartACE::NAMING::instance()->init(parameter.getAllParametersFromGroup("component")) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		} else {
			if(SmartACE::NAMING::instance()->init(argc, argv) != 0) {
				// initialization of naming service failed
				throw std::logic_error( "<NamingService> Service initialization failed!\nPossible causes could be:\n-> Erroneous configuration.\n-> Naming service not reachable.\n" );
			}
		}
			
		// print all known parameters
		// parameter.print();
		
		//--- server port // client port // other parameter ---
		// load parameter
		parameter.getString("component", "name", connections.component.name);
		parameter.getString("component", "initialComponentMode", connections.component.initialComponentMode);
		if(parameter.checkIfParameterExists("component", "defaultScheduler")) {
			parameter.getString("component", "defaultScheduler", connections.component.defaultScheduler);
		}
		if(parameter.checkIfParameterExists("component", "useLogger")) {
			parameter.getBoolean("component", "useLogger", connections.component.useLogger);
		}
		
		// load parameters for client LaserSafetyEventServiceIn
		parameter.getBoolean("LaserSafetyEventServiceIn", "initialConnect", connections.laserSafetyEventServiceIn.initialConnect);
		parameter.getString("LaserSafetyEventServiceIn", "serviceName", connections.laserSafetyEventServiceIn.serviceName);
		parameter.getString("LaserSafetyEventServiceIn", "serverName", connections.laserSafetyEventServiceIn.serverName);
		parameter.getString("LaserSafetyEventServiceIn", "wiringName", connections.laserSafetyEventServiceIn.wiringName);
		if(parameter.checkIfParameterExists("LaserSafetyEventServiceIn", "roboticMiddleware")) {
			parameter.getString("LaserSafetyEventServiceIn", "roboticMiddleware", connections.laserSafetyEventServiceIn.roboticMiddleware);
		}
		// load parameters for client TaskResultIn
		parameter.getBoolean("TaskResultIn", "initialConnect", connections.taskResultIn.initialConnect);
		parameter.getString("TaskResultIn", "serviceName", connections.taskResultIn.serviceName);
		parameter.getString("TaskResultIn", "serverName", connections.taskResultIn.serverName);
		parameter.getString("TaskResultIn", "wiringName", connections.taskResultIn.wiringName);
		parameter.getInteger("TaskResultIn", "interval", connections.taskResultIn.interval);
		if(parameter.checkIfParameterExists("TaskResultIn", "roboticMiddleware")) {
			parameter.getString("TaskResultIn", "roboticMiddleware", connections.taskResultIn.roboticMiddleware);
		}
		// load parameters for client kbQueryClient
		parameter.getBoolean("kbQueryClient", "initialConnect", connections.kbQueryClient.initialConnect);
		parameter.getString("kbQueryClient", "serviceName", connections.kbQueryClient.serviceName);
		parameter.getString("kbQueryClient", "serverName", connections.kbQueryClient.serverName);
		parameter.getString("kbQueryClient", "wiringName", connections.kbQueryClient.wiringName);
		if(parameter.checkIfParameterExists("kbQueryClient", "roboticMiddleware")) {
			parameter.getString("kbQueryClient", "roboticMiddleware", connections.kbQueryClient.roboticMiddleware);
		}
		
		// load parameters for server TaskEventOut
		parameter.getString("TaskEventOut", "serviceName", connections.taskEventOut.serviceName);
		if(parameter.checkIfParameterExists("TaskEventOut", "roboticMiddleware")) {
			parameter.getString("TaskEventOut", "roboticMiddleware", connections.taskEventOut.roboticMiddleware);
		}
		
		// load parameters for task CommandResultTask
		parameter.getDouble("CommandResultTask", "minActFreqHz", connections.commandResultTask.minActFreq);
		parameter.getDouble("CommandResultTask", "maxActFreqHz", connections.commandResultTask.maxActFreq);
		parameter.getString("CommandResultTask", "triggerType", connections.commandResultTask.trigger);
		if(connections.commandResultTask.trigger == "PeriodicTimer") {
			parameter.getDouble("CommandResultTask", "periodicActFreqHz", connections.commandResultTask.periodicActFreq);
		} else if(connections.commandResultTask.trigger == "DataTriggered") {
			parameter.getString("CommandResultTask", "inPortRef", connections.commandResultTask.inPortRef);
			parameter.getInteger("CommandResultTask", "prescale", connections.commandResultTask.prescale);
		}
		if(parameter.checkIfParameterExists("CommandResultTask", "scheduler")) {
			parameter.getString("CommandResultTask", "scheduler", connections.commandResultTask.scheduler);
		}
		if(parameter.checkIfParameterExists("CommandResultTask", "priority")) {
			parameter.getInteger("CommandResultTask", "priority", connections.commandResultTask.priority);
		}
		if(parameter.checkIfParameterExists("CommandResultTask", "cpuAffinity")) {
			parameter.getInteger("CommandResultTask", "cpuAffinity", connections.commandResultTask.cpuAffinity);
		}
		// load parameters for task ReadCommandTask
		parameter.getDouble("ReadCommandTask", "minActFreqHz", connections.readCommandTask.minActFreq);
		parameter.getDouble("ReadCommandTask", "maxActFreqHz", connections.readCommandTask.maxActFreq);
		parameter.getString("ReadCommandTask", "triggerType", connections.readCommandTask.trigger);
		if(connections.readCommandTask.trigger == "PeriodicTimer") {
			parameter.getDouble("ReadCommandTask", "periodicActFreqHz", connections.readCommandTask.periodicActFreq);
		} else if(connections.readCommandTask.trigger == "DataTriggered") {
			parameter.getString("ReadCommandTask", "inPortRef", connections.readCommandTask.inPortRef);
			parameter.getInteger("ReadCommandTask", "prescale", connections.readCommandTask.prescale);
		}
		if(parameter.checkIfParameterExists("ReadCommandTask", "scheduler")) {
			parameter.getString("ReadCommandTask", "scheduler", connections.readCommandTask.scheduler);
		}
		if(parameter.checkIfParameterExists("ReadCommandTask", "priority")) {
			parameter.getInteger("ReadCommandTask", "priority", connections.readCommandTask.priority);
		}
		if(parameter.checkIfParameterExists("ReadCommandTask", "cpuAffinity")) {
			parameter.getInteger("ReadCommandTask", "cpuAffinity", connections.readCommandTask.cpuAffinity);
		}
		// load parameters for task StatusSerialTask
		parameter.getDouble("StatusSerialTask", "minActFreqHz", connections.statusSerialTask.minActFreq);
		parameter.getDouble("StatusSerialTask", "maxActFreqHz", connections.statusSerialTask.maxActFreq);
		parameter.getString("StatusSerialTask", "triggerType", connections.statusSerialTask.trigger);
		if(connections.statusSerialTask.trigger == "PeriodicTimer") {
			parameter.getDouble("StatusSerialTask", "periodicActFreqHz", connections.statusSerialTask.periodicActFreq);
		} else if(connections.statusSerialTask.trigger == "DataTriggered") {
			parameter.getString("StatusSerialTask", "inPortRef", connections.statusSerialTask.inPortRef);
			parameter.getInteger("StatusSerialTask", "prescale", connections.statusSerialTask.prescale);
		}
		if(parameter.checkIfParameterExists("StatusSerialTask", "scheduler")) {
			parameter.getString("StatusSerialTask", "scheduler", connections.statusSerialTask.scheduler);
		}
		if(parameter.checkIfParameterExists("StatusSerialTask", "priority")) {
			parameter.getInteger("StatusSerialTask", "priority", connections.statusSerialTask.priority);
		}
		if(parameter.checkIfParameterExists("StatusSerialTask", "cpuAffinity")) {
			parameter.getInteger("StatusSerialTask", "cpuAffinity", connections.statusSerialTask.cpuAffinity);
		}
		
		
		// load parameters for all registered component-extensions
		for(auto extension = componentExtensionRegistry.begin(); extension != componentExtensionRegistry.end(); extension++) 
		{
			extension->second->loadParameters(parameter);
		}
		
		paramHandler.loadParameter(parameter);
	
	} catch (const SmartACE::IniParameterError & e) {
		std::cerr << e.what() << std::endl;
	} catch (const std::exception &ex) {
		std::cerr << "Uncaught std::exception: " << ex.what() << std::endl;
	} catch (...) {
		std::cerr << "Uncaught exception" << std::endl;
	}
}
